// Input data will contain the JSON from the webhook
const resumeData = $input.all().json;

// Load required libraries
const PDFDocument = require('pdfkit');

// Function to create PDF from resume data
function createResumePDF(resumeText) {
  // Create a PDF document buffer
  const pdfBuffer = [];
  const doc = new PDFDocument({ margin: 50 });

  // Pipe the PDF into a buffer
  doc.on('data', pdfBuffer.push.bind(pdfBuffer));

  // Split content by sections (double newlines)
  const sections = resumeText.split('\n\n');

  // Parse header (name and contact info)
  const headerLines = sections[0].split('\n');
  const name = headerLines[0];
  const contactInfo = headerLines[1];

  // Add the name as a title
  doc.fontSize(18).font('Helvetica-Bold').text(name, { align: 'center' });
  doc.moveDown(0.5);

  // Add contact info
  doc.fontSize(10).font('Helvetica').text(contactInfo, { align: 'center' });
  doc.moveDown(1);

  // Process remaining sections
  for (let i = 1; i < sections.length; i++) {
    const section = sections[i];

    // Check if this is a section header (starts with **)
    if (section.startsWith('**')) {
      // Extract section name
      const sectionTitle = section.split('\n')[0].replace(/\*\*/g, '');

      doc.fontSize(14).font('Helvetica-Bold').text(sectionTitle);
      doc.moveDown(0.5);

      // Extract and format section content (remove the title line)
      const contentLines = section.split('\n').slice(1);

      // Process bullet points and regular text
      contentLines.forEach(line => {
        if (line.trim().startsWith('*')) {
          // This is a bullet point
          const bulletText = line.trim().substring(1).trim();
          doc.fontSize(10).font('Helvetica')
             .text(bulletText, { indent: 20, bulletPoint: '•' });
        } else if (line.trim()) {
          // Regular text
          doc.fontSize(10).font('Helvetica').text(line.trim());
        }

        // Small spacing between lines if needed
        if (line.trim()) doc.moveDown(0.2);
      });
    } else {
      // Regular paragraph
      doc.fontSize(10).font('Helvetica').text(section);
    }

    doc.moveDown(1);
  }

  // Finalize the PDF
  doc.end();

  return Buffer.concat(pdfBuffer).toString('base64');
}

// Process the data and generate PDF
const pdfBase64 = createResumePDF(resumeData);

// Return the PDF for the next node
return {
  json: {
    pdfData: pdfBase64,
    contentType: 'application/pdf',
    filename: 'resume.pdf'
  }
};
